class ScrollbarFrame(wx.Frame):
    def __init__(self):
        wx.Frame.__init__(self, None, -1, 'Scrollbar Example', 
                size=(800, len(lst1)*200))
        self.scroll = wx.ScrolledWindow(self, -1)
        self.scroll.SetScrollbars(1, 1, 800, len(lst1)*200)
        self.scroll.SetBackgroundColour('BLACK')
        index1 = 0
        index2 = 0
        
        for pic1 in lst1:
            bmp1 = wx.Image(("B:\\screening\\" + 'HS01' + "\\cutout\\"
                + str(pic1)), type=wx.BITMAP_TYPE_JPEG)           
            bmp1 = bmp1.ConvertToBitmap()
            pos = bmp1.GetHeight()
            self.bitmap1 = wx.StaticBitmap(self.scroll, -1, bmp1, (30, 100+index1),
                         (bmp1.GetWidth(), bmp1.GetHeight()))
            index1 = index1 + 150
        
        for pic2 in lst2:             
            bmp2 = wx.Image(("B:\\screening\\" + 'HS01' + "\\cutout\\"
                + str(pic2)), type=wx.BITMAP_TYPE_JPEG)           
            bmp2 = bmp2.ConvertToBitmap()
            pos2 = bmp2.GetHeight()
            self.bitmap2 = wx.StaticBitmap(self.scroll, -1, bmp2, (500, 100+index2),
                         (bmp2.GetWidth(), bmp2.GetHeight()))
            index2 = index2 + 150
                                           

    def OnClickTop(self, event):
        self.scroll.Scroll(600, 400)
        
    def OnClickBottom(self, event):
        self.scroll.Scroll(1, 1)
        
if __name__ == '__main__':
    app = wx.PySimpleApp()
    frame = ScrollbarFrame()
    frame.Show()
    app.MainLoop()


